import { useState, useEffect } from "react";
import { db } from "./firebase";
import {
  collection,
  addDoc,
  updateDoc,
  deleteDoc,
  doc,
  onSnapshot,
} from "firebase/firestore";

export default function GroceryList() {
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState("");

  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, "groceryItems"), (snapshot) => {
      const itemList = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setItems(itemList);
    });
    return () => unsubscribe();
  }, []);

  const addItem = async () => {
    if (!newItem) return;
    await addDoc(collection(db, "groceryItems"), {
      name: newItem,
      quantity: 1,
      purchased: false,
      createdAt: new Date().toISOString(),
    });
    setNewItem("");
  };

  const togglePurchased = async (id, purchased) => {
    await updateDoc(doc(db, "groceryItems", id), { purchased: !purchased });
  };

  const adjustQuantity = async (id, quantity) => {
    await updateDoc(doc(db, "groceryItems", id), { quantity });
  };

  const removeItem = async (id) => {
    await deleteDoc(doc(db, "groceryItems", id));
  };

  return (
    <div className="container">
      <h1>Grocery List</h1>
      <div className="input-container">
        <input
          type="text"
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
          placeholder="Add a new item"
        />
        <button onClick={addItem}>Add</button>
      </div>
      <ul>
        {items.map((item) => (
          <li key={item.id} className="grocery-item">
            <input
              type="checkbox"
              checked={item.purchased}
              onChange={() => togglePurchased(item.id, item.purchased)}
            />
            <span className={`grocery-item ${item.purchased ? 'purchased' : ''}`}>
                {item.name} ({item.quantity})
</span>
            <span className="date">{new Date(item.createdAt).toLocaleString()}</span>
            <div>
              <button onClick={() => adjustQuantity(item.id, item.quantity - 1)}>-</button>
              <button onClick={() => adjustQuantity(item.id, item.quantity + 1)}>+</button>
              <button onClick={() => removeItem(item.id)}>ðŸ—‘</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
