import { useState, useEffect } from "react";
import { db } from "./firebase";
import {
  collection,
  addDoc,
  updateDoc,
  deleteDoc,
  doc,
  onSnapshot,
} from "firebase/firestore";

export default function GroceryList() {
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState("");
  const [editingItem, setEditingItem] = useState(null);
  const [editText, setEditText] = useState("");

  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, "groceryItems"), (snapshot) => {
      const itemList = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setItems(itemList);
    });
    return () => unsubscribe();
  }, []);

  const addItem = async () => {
    if (!newItem) return;
    await addDoc(collection(db, "groceryItems"), {
      name: newItem,
      quantity: 1,
      purchased: false,
      createdAt: new Date().toISOString(),
    });
    setNewItem("");
  };

  const removeItem = async (id) => {
    await deleteDoc(doc(db, "groceryItems", id));
  };

  const editItem = (item) => {
    setEditingItem(item.id);
    setEditText(item.name);
  };

  const saveEdit = async (id) => {
    if (!editText.trim()) return;
    await updateDoc(doc(db, "groceryItems", id), { name: editText });
    setEditingItem(null);
  };

  return (
    <div className="container">
      <h1>Grocery List</h1>
      <div className="input-container">
        <input
          type="text"
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
          placeholder="Add a new item"
        />
        <button onClick={addItem}>Add</button>
      </div>

      <ul>
        {items.map((item, index) => (
          <li key={item.id} className="flex justify-between items-center p-3 border-b">
            {/* Leftmost: Item number */}
            <span className="text-lg font-bold">{index + 1}.</span>

            {/* Middle: Item Name & Timestamp */}
            <div className="flex flex-col">
              {editingItem === item.id ? (
                <input
                  type="text"
                  value={editText}
                  onChange={(e) => setEditText(e.target.value)}
                  className="border p-1 rounded"
                />
              ) : (
                <span className="grocery-item text-xl font-bold">{item.name} ({item.quantity})</span>
              )}
              <span className="text-xs text-gray-500">{new Date(item.createdAt).toLocaleString()}</span>
            </div>

            {/* Rightmost: Edit & Delete Buttons */}
            <div className="flex space-x-2">
              {editingItem === item.id ? (
                <button onClick={() => saveEdit(item.id)} className="bg-green-500 text-white px-3 py-1 rounded">‚úÖ Save</button>
              ) : (
                <button onClick={() => editItem(item)} className="bg-yellow-500 text-white px-3 py-1 rounded">‚úèÔ∏è Edit</button>
              )}
              <button onClick={() => removeItem(item.id)} className="bg-red-500 text-white px-3 py-1 rounded">üóë Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
