import { useState, useEffect } from "react";
import { db } from "./firebase";
import {
  collection,
  addDoc,
  updateDoc,
  deleteDoc,
  doc,
  onSnapshot,
} from "firebase/firestore";

export default function GroceryList() {
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState("");
  const [editingItem, setEditingItem] = useState(null);
  const [editText, setEditText] = useState("");

  // Fetch items from Firestore
  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, "groceryItems"), (snapshot) => {
      const itemList = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setItems(itemList);
    });
    return () => unsubscribe();
  }, []);

  // Add a new item
  const addItem = async () => {
    if (!newItem) return;
    await addDoc(collection(db, "groceryItems"), {
      name: newItem,
      quantity: 1,
      purchased: false,
      createdAt: new Date().toISOString(),
    });
    setNewItem("");
  };

  // Remove an item
  const removeItem = async (id) => {
    await deleteDoc(doc(db, "groceryItems", id));
  };

  // Edit an item
  const editItem = (item) => {
    setEditingItem(item.id);
    setEditText(item.name);
  };

  // Save edited item
  const saveEdit = async (id) => {
    if (!editText.trim()) return;
    await updateDoc(doc(db, "groceryItems", id), { name: editText });
    setEditingItem(null);
  };

  // Toggle purchased status
  const togglePurchased = async (id, purchased) => {
    await updateDoc(doc(db, "groceryItems", id), { purchased: !purchased });
  };

  return (
    <div className="container">
      <h1>Grocery List</h1>
      <div className="input-container">
        <input
          type="text"
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
          placeholder="Add a new item"
        />
        <button onClick={addItem}>Add</button>
      </div>

      <ul>
        {items.map((item) => (
          <span className={`grocery-item ${item.purchased ? "line-through" : ""}`}>
          {item.name}
        </span>
        ))}
      </ul>
    </div>
  );
}
