import { useState, useEffect } from "react";
import { db } from "./firebase";
import {
  collection,
  addDoc,
  updateDoc,
  deleteDoc,
  doc,
  onSnapshot,
} from "firebase/firestore";

export default function GroceryList() {
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState("");
  const [editingItem, setEditingItem] = useState(null);
  const [editText, setEditText] = useState("");

  function editItem(item) {
    setEditingItem(item.id);
    setEditText(item.name);
  }



  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, "groceryItems"), (snapshot) => {
      const itemList = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setItems(itemList);
    });
    return () => unsubscribe();
  }, []);

  const addItem = async () => {
    if (!newItem) return;
    await addDoc(collection(db, "groceryItems"), {
      name: newItem,
      quantity: 1,
      purchased: false,
      createdAt: new Date().toISOString(),
    });
    setNewItem("");
  };

  const togglePurchased = async (id, purchased) => {
    await updateDoc(doc(db, "groceryItems", id), { purchased: !purchased });
  };

  const adjustQuantity = async (id, quantity) => {
    await updateDoc(doc(db, "groceryItems", id), { quantity });
  };

  const removeItem = async (id) => {
    await deleteDoc(doc(db, "groceryItems", id));
  };

  async function saveEdit(id) {
    if (!editText) return;
    try {
      await client.graphql({
        query: updateGroceryItem,
        variables: { input: { id, name: editText } },
      });
      setEditingItem(null);
      fetchGroceries();
    } catch (error) {
      console.error('Error updating item:', error);
    }
  }

  return (
    <div className="container">
      <h1>Grocery List</h1>
      <div className="input-container">
        <input
          type="text"
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
          placeholder="Add a new item"
        />
        <button onClick={addItem}>Add</button>
      </div>
      <ul>
        {items.map((item, index) => (
        <li key={item.id} className="flex justify-between items-center p-3 border-b">
            <span className="text-lg font-bold">{index + 1}.</span>

            <div className="flex flex-col">
                <span className={`grocery-item ${item.purchased ? 'purchased' : ''}`}>
                    {item.name} ({item.quantity})
                </span>
                <span className="text-xs text-gray-500">{new Date(item.createdAt).toLocaleString()}</span>
            </div>

            <div className="flex space-x-2">
                <button onClick={() => editItem(item)} className="bg-yellow-500 text-white px-3 py-1 rounded">‚úèÔ∏è Edit</button>
                <button onClick={() => removeItem(item.id)} className="bg-red-500 text-white px-3 py-1 rounded">üóë Delete</button>
            </div>
         </li>
  ))}
</ul>

    </div>
  );
}
